	void MapDecoder::buildBackgroundLayers()
	{
		_layers.push_back(new Layer("Resources/Images/Backgrounds/sky.png", _pixleSize, 1));
		_layers.push_back(new Layer("Resources/Images/Backgrounds/clouds.png", _pixleSize, 134));
		_layers.push_back(new Layer("Resources/Images/Backgrounds/sea.png", _pixleSize, 610));
		_layers.push_back(new Layer("Resources/Images/Backgrounds/tileset.png", _pixleSize, 652));
	}

	void MapDecoder::releaseLayers()
	{
		for (int i = 0; i < _layers.size(); i++)
		{
			delete _layers[i];
		}

		_layers.clear();
	}


	// draw XML mapping to the window (multi layers i.e. multi tilesets)
	void MapDecoder::decodeXML(int* encodingArray, Layers layers)
	{
		sf::Sprite pixle;

		for (int i = 0; i < _width * _height; i++)
		{
			int code = encodingArray[i];

			if (code == 0)
				continue;

			for (int j = layers.size() - 1; j >= 0; j--)
			{
				if (code >= layers[j]->getFirstTile())
				{
					code -= layers[j]->getFirstTile() - 1;

					pixle = getPixle(layers[j], code);
					break;
				}
			}

			pixle.setPosition(drawingPosition(i).x, drawingPosition(i).y);

			_window.draw(pixle);
		}
	}


	// draw XML mapping to the window 
	void MapDecoder::decodeXML(int* encodingArray, Layer* layer)
	{
		sf::Sprite pixle;

		for (int i = 0; i < _width * _height; i++)
		{
			int code = encodingArray[i];

			if (code == 0)
				continue;

			code -= layer->getFirstTile() - 1;
			pixle = getPixle(layer, code);

			sf::Vector2f position = drawingPosition(i);
			pixle.setPosition(position.x, position.y);

			_window.draw(pixle);
		}
	}


	sf::Sprite MapDecoder::getPixle(Layer* layer, int code)
	{
		sf::Vector2i pixlePosition = layer->decode(code);
		return layer->getPixleAt(pixlePosition.x, pixlePosition.y);
	}


	// xml array index to window drawing position
	sf::Vector2f MapDecoder::drawingPosition(int encoding)
	{
		int x = 0;
		int y = 0;

		while (encoding > _width - 1)
		{
			encoding -= _width;
			y++;
		}

		x = encoding;

		return sf::Vector2f(x * _pixleSize, y * _pixleSize);
	}


	Layers MapDecoder::subVector(Layers layers, int startingLayer, int layerCount)
	{
		Layers::const_iterator first = layers.begin() + (startingLayer - 1);
		Layers::const_iterator last = layers.begin() + (startingLayer - 1 + layerCount);

		return Layers(first, last);
	}

